package javafullcourse;

public class Unaryoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		//a++;
		

		 System.out.println( a++);         //post aaitu increment aguthu so once again keela print pannanum
		 System.out.println( a);    
		 
		 System.out.println( ++a);  // increment aaitu thsan post aaguhu

	}

}
