package javafullcoursepart2;

public class stringmaths {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Absolute : "+Math.abs(-48));
		System.out.println("Round : "+Math.round(3.56));//round off the value
		System.out.println("Ceil : "+Math.ceil(3.56)); //print the nearest next value
		System.out.println("Floor : "+Math.floor(3.56));//remove the decimal points
		
		double a=12;
		double b=2;
		System.out.println(Math.max(a, b));
		System.out.println(Math.sqrt(a));
		System.out.println(Math.pow(a, b));
		System.out.println(Math.log(a));
		System.out.println(Math.log10(a));
		System.out.println(Math.sin(a));
		System.out.println(Math.cos(a));
		System.out.println(Math.tan(a));

		
		
	}

}
